<?php/** * People Widget Template * * Produces output for appropriate widget class in framework. * $this, $instance (sanitized field values) and $args are available. * * $this->ctfw_get_posts() can be used to produce a query according to widget field values. */// No direct accessif ( ! defined( 'ABSPATH' ) ) exit;// HTML Beforeecho $args['before_widget'];// Title$title = apply_filters( 'widget_title', $instance['title'] );if ( ! empty( $title ) ) {	echo $args['before_title'] . $title . $args['after_title'];}// Get posts$posts = $this->ctfw_get_posts(); // widget's default query according to field values// Loop Posts$i = 0;foreach ( $posts as $post ) : setup_postdata( $post ); $i++;	// Get people meta data	// $position	extract( ctfw_person_data() );?>	<article <?php post_class( 'churchy-widget-entry churchy-people-widget-entry churchy-clearfix' . ( 1 == $i ? ' churchy-widget-entry-first' : '' ) ); ?>>		<header class="churchy-clearfix">			<?php if ( $instance['show_image'] && has_post_thumbnail() ) : ?>				<div class="churchy-widget-entry-thumb">					<a href="<?php the_permalink(); ?>" title="<?php the_title_attribute(); ?>"><?php the_post_thumbnail( 'churchy-thumb-small', array( 'class' => 'churchy-image' ) ); ?></a>				</div>			<?php endif; ?>			<h5 class="churchy-widget-entry-title"><a href="<?php the_permalink(); ?>" title="<?php the_title_attribute(); ?>"><?php the_title(); ?></a></h5>			<ul class="churchy-widget-entry-meta churchy-clearfix">				<?php if ( $instance['show_position'] && $position ) : ?>					<li class="churchy-people-widget-entry-position">						<?php echo esc_html( $position ); ?>					</li>				<?php endif; ?>				<?php if ( $instance['show_phone'] && $phone ) : ?>					<li class="churchy-people-widget-entry-phone">						<?php echo esc_html( $phone ); ?>					</li>				<?php endif; ?>				<?php if ( $instance['show_email'] && $email ) : ?>					<li class="churchy-people-widget-entry-email">						<a href="mailto:<?php echo antispambot( $email, true ); ?>"><?php echo antispambot( $email ); ?></a>					</li>				<?php endif; ?>			</ul>		</header>		<?php if ( get_the_excerpt() && ! empty( $instance['show_excerpt'] )): ?>			<div class="churchy-widget-entry-content">				<?php the_excerpt(); ?>			</div>		<?php endif; ?>	</article><?php// End Loopendforeach;// No items foundif ( empty( $posts ) ) {	?>	<div>		<?php _ex( 'There are no people to show.', 'people widget', 'churchy' ); ?>	</div>	<?php}// HTML Afterecho $args['after_widget'];